import React, { Component } from 'react';

const ProductContext = React.createContext();



 class ProductProvider extends Component {
                 state = {
                   name: '',
                   number: '',
                   message: '',
                   email: '',
                   messageSent: false,
     };
     
     setField = (targetID, textValue) => {
         this.setState(() => {
           return {
             lastProduct: lastItem,
             products: tempProducts,
             clothing: tempClothingProducts,
             gadget: tempGadgetProducts,
             tollsAndEssentials: tempGadgets,
             smoke: tempSmokeProducts,
             mens: tempMensProducts,
             womens: tempWomensProducts,
             phoneAccessories: tempPhoneAccessories,
             pc: tempPcAccessories,
             deviceAccessories: tempPhoneAccessories.concat(tempPcAccessories),
             waterPipes: tempWaterPipes,
             smokeAccessories: tempAccessories,
             latest: tempLatest,
             trending: tempTrending,
           };
         });
         
     }

                 render() {
                   return (
                     <ProductContext.Provider
                       value={{
                         ...this.state,
                         
                       }}
                     >
                       {this.props.children}
                     </ProductContext.Provider>
                   );
                 }
               }

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer, ProductContext };
