import React,{useContext,} from 'react'
import { Form, FormGroup, Label, Input } from 'reactstrap';
import { webAddOns, appAddOns } from '../data/business-services/index';
import { Button } from './common/button';
import { InputBoxes } from '../components/addOns/inputBoxes';
import Fade from 'react-reveal/Fade';
import { ProductContext } from '../pages/oniContext';

export const SelectAddons = () => {
    const pCR = useContext(ProductContext);
    return (
      <div style={{ 'text-align': 'center' }}>
        <Form>
          <div className="list-buttons">
            <Button
              Class="button-rectangular btn button2 gradient-color"
              Name={'Web App Packages'}
              Clickble={() =>
                pCR.handleActAdO('webPackages', false)
              }
              iconFalse={true}
              BtnIcon="btn-icon"
              type="touchAction"
            >
              Do Something
            </Button>{' '}
            <Button
              Class="button-rectangular btn button2 gradient-color"
              Name={'Mobile Application Packages'}
              Clickble={() =>
                productConsumer.handleActPackage('mobileAppPackages', false)
              }
              iconFalse={true}
              BtnIcon="btn-icon"
              type="touchAction"
            >
              Cancel
            </Button>
          </div>
          <div className="list-buttons">
            <Button
              Class="button-rectangular btn button2 gradient-color"
              Name={'Software Packages'}
              Clickble={() =>
                productConsumer.handleActPackage(
                  'softwareDevelopmentPackages',
                  false
                )
              }
              iconFalse={true}
              BtnIcon="btn-icon"
              type="touchAction"
            />
            <Button
              Class="button-rectangular btn button2 gradient-color"
              Name={'Marketing  Packages'}
              Clickble={() =>
                productConsumer.handleActPackage('seoMarketingPackages', false)
              }
              iconFalse={true}
              BtnIcon="btn-icon"
              type="touchAction"
            />
          </div>
          <div>
            {pCR.activeAddOns.length > 0 && (
            <Fade>
              <InputBoxes />
            </Fade>
          )}
          </div>
          
        </Form>
      </div>
    );
}
