import React, { useState,useContext,useEffect } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ProductContext } from '../../pages/oniContext';
import { webAppPackages } from '../../data/business-services';
import {CardComp} from '../cards/card'
import './modal.scss'
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import { Carousel } from 'react-responsive-carousel';

const ModalExample = (props) => {
  const productConsumer = useContext(ProductContext);

  useEffect(() => {
   console.log(productConsumer.isModalOpen,'Modal')
  }, [])
  const { buttonLabel, className } = props;



  return (
    <div>
      <Button color="danger" onClick={productConsumer.handleModal}>
        {buttonLabel}
      </Button>
      <Modal
        isOpen={productConsumer.isModalOpen}
        toggle={productConsumer.handleModal}
        className={className}
      >
        <ModalHeader toggle={productConsumer.handleModal}>
          Choose a package
        </ModalHeader>
        <ModalBody>
          <Carousel>
            {webAppPackages.map((service, index) => (
              <CardComp
                title={service.title}
                text={service.content}
                addOns={service.addOns}
              />
            ))}
          </Carousel>
        </ModalBody>

        <ModalFooter>
          <Button color="primary" onClick={productConsumer.handleModal}>
            Do Something
          </Button>{' '}
          <Button color="secondary" onClick={productConsumer.handleModal}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default ModalExample;
