import React, { Component,useState } from 'react';

const ProductContext = React.createContext();



 const oniContext = (props) => {
    const [state, setstate] = useState({
      name: '',
      number: '',
      message: '',
      email: '',
      messageSent: false,
    });
    return (
      <ProductContext.Provider
        value={{
          ...state,
        }}
      >
        {props.children}
      </ProductContext.Provider>
    );
}


