        toggle={productConsumer.handleModal}
        className="modal_div"
      >
        <ModalHeader toggle={productConsumer.handleModal}>
          Choose a package
        </ModalHeader>
        <ModalBody>
          <SelectPackage />
          <QuoteSteps />
        </ModalBody>

        <ModalFooter>
          
            <div style={{'margin-right': '7em'}}>
              <PrevButton
                Class="button1 btn button2 gradient-color"
                Name={'Previous'}
                Clickble={productConsumer.handleSubmit}
               
                BtnIcon="btn-icon"
                type="navigation"
                
              />
            </div>
            <div>
              <Button
                Class="button1 btn button2 gradient-color"
                Name={!productConsumer.messageSent ? 'SUBMIT' : 'SENT'}
                Clickble={productConsumer.handleSubmit}
                send={productConsumer.messageSent}
                BtnIcon="btn-icon"
                type="contact"
                onClick={productConsumer.handleModal}
              />
            </div>
         
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default ModalExample;
